import inspect


try:
    getfullargspec = inspect.getfullargspec
except AttributeError:
    from collections import namedtuple

    FullArgSpec = namedtuple(
        "FullArgSpec",
        "args, varargs, varkw, defaults, kwonlyargs, kwonlydefaults, annotations",
    )

    def getfullargspec(f):
        if f is object.__init__:
            f = lambda self: None  # noqa: E731
        params, varargs, varkw, defaults = inspect.getargspec(f)
        kwonly, kwonly_defaults, annotations = None, None, None
        return FullArgSpec(
            params, varargs, varkw, defaults, kwonly, kwonly_defaults, annotations
        )
